  `test_instruction` text NOT NULL,
  `test_results` text NOT NULL,
  `idcategory_test` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Дамп данных таблицы `test`
--

INSERT INTO `test` (`idtest`, `test_name`, `test_description`, `test_instruction`, `test_results`, `idcategory_test`) VALUES
(1, '<h1>Шкала тревоги Бека</h1>', '<p>Шкала тревоги Бека – клиническая тестовая методика, предназначенная для предварительной оценки степени выраженности тревожных расстройств у широкого круга лиц: молодых людей от 14 лет, зрелых и пожилых, контингета клиники и при скрининговых исследованиях. Обычно шкалу используют при профосмотрах, когда необходимо выделить контингент, нуждающийся в более подробном обследовании и консультации специалиста.</p>\r\n\r\n<p>Автор: Аарон Т. Бек и др. (Aaron T. Beck, N. Epstein, G. Brown, R. A. Steer, 1988)</p>\r\n\r\n<p>Адаптация: Н. В. Тарабрина (предположительно)</p>', '<p>Предложенный список содержит наиболее распространенные симптомы тревоги. Пожалуйста, тщательно изучите каждый пункт. Отметьте, насколько вас беспокоил каждый из этих симптомов в течение последней недели, включая сегодняшний день.</p>', '<p>Зачение до 21 балла включительно свидетельствует о незначительном уровне тревоги.</p>\r\n\r\n<p>Значение от 22 до 35 баллов означает среднюю выраженность тревоги</p>\r\n\r\n<p>Значение выше 36 баллов (при максимуме в 63 балла) свидетельствует об очень высокой тревоге.</p>', 3),
(2, 'ШКАЛА (ТЕСТ-ОПРОСНИК) ДЕПРЕССИИ БЕКА. ', 'Методика Шкала депрессии Бека используется для диагностики уровня депрессии.\r\nТест-опросник депрессии (Beck Depression Inventory) был предложен Аароном Т.\r\nБеком в 1961 году на основе клинических наблюдений, позволивших выявить перечень\r\nсимптомов депрессии.\r\nПосле сравнения этого списка с клиническими описаниями депрессии, был создан\r\nтест-опросник депрессии, включающий в себя 21 вопрос-утверждение наиболее часто\r\nвстречаемых симптомов и жалоб.\r\nКаждый пункт опросника состоит из 4-5 утверждений, соответствующих\r\nспецифическим проявлениям/симптомам депрессии. Эти утверждения ранжированы по\r\nмере увеличения удельного веса симптома в общей степени тяжести депрессии. ', 'Вам предлагается ряд утверждений. Выберите одно утверждение в\r\nкаждой группе, которое лучше всего описывает Ваше состояние за прошедшую неделю,\r\nвключая сегодняшний день. Обведите кружком номер утверждения, которое Вы выбрали.\r\nЕсли в группе несколько утверждений в равной мере хорошо описывают Ваше состояние,\r\nобведите каждое из них. Прежде чем сделать выбор, внимательно прочтите все\r\nутверждения в каждой группе. ', 'Менее 9 – отсутствие депрессивных симптомов.\r\n10-18 – легкая депрессия, астено-субдепрессивная симптоматика, м.б. у соматических\r\nбольных или невротический уровень.\r\n19-29 – умеренная депрессия, критический уровень.\r\n30-63 – явно выраженная депрессивная симптоматика, не исключена эндогенность.', 1),
(3, 'Диагностика принятия других Фейя.', 'Диагностика принятия других (Acceptance of Others Scale) – личностный опросник, предназначенный для диагностики уровня принятия других людей, прежде всего у школьников и первокурсников.\r\n\r\nАвтор: Уильям Фей (William F. Fey, 1955)', 'Внимательно прочитайте утверждения опросника. Если вы считаете, что согласны с данным утверждением и оно соответствует вашему представлению о себе и других людях, то отметьте степень вашего согласия с ним, используя предложенную шкалу.', '60 баллов и больше — высокий показатель принятия других;\r\n45—59 баллов — средний показатель принятия других с тенденцией к высокому;\r\n30—44 балла — средний показатель принятия других с тенденцией к низкому;\r\n29 и меньше баллов — низкий показатель принятия других.\r\nСледует отметить, что низкие показатели принятия других не только способствуют активизации защитного поведения, но и являются профессионально негативным качеством для всех, кто по роду своей деятельности должен осуществлять функцию организации и управления работой других людей.\r\n\r\nДругим состоянием, тесно связанным с активизацией психологической защиты является враждебность и агрессия. Предлагаем оценить уровень данного состояния по предлагаемой ниже методике.', 2);

